###############################
####perform validation tests###
###############################

validation.set<-subset(raw.data,!is.na(Female))

#first for female
#assign quantities from data
scores<-validation.set$Female_Prediction
binary.labels <- validation.set$Female == 1

#assign threshold
threshold<-0.5

#extract classification statistics
tp <- sum( (scores > threshold) & binary.labels )
sensitivity <- tp / sum(binary.labels)
tn <- sum( (scores <= threshold) & (! binary.labels))
specificity <- tn / sum(!binary.labels)
fp<-sum( (scores > threshold) & (! binary.labels))
fn<-sum( (scores <= threshold) & binary.labels)
False.Positive.Rate<-1-specificity
False.Negative.Rate<-fn/(fn+tp)
PPV<-tp/(tp+fp)
F1<-2*((PPV*sensitivity)/(PPV+sensitivity))
Accuracy<-(tp+tn)/(tp+tn+fp+fn)

#store results
Store.0<-cbind(PPV,sensitivity,F1,Accuracy)
colnames(Store.0)<-c("Precision","Recall","F1","Accuracy")
Store.0

#now for male
#assign quantities from data
scores<-ifelse(validation.set$Female_Prediction==1,0,ifelse(validation.set$Female_Prediction==0,1,NA))
binary.labels <- validation.set$Male == 1

